function initMap(){var d={lat:58.378263,lng:26.714566};var c=new google.maps.Map(document.getElementById("map"),{zoom:18,center:d});var a=new google.maps.Marker({position:d,icon:location.protocol+"//"+location.hostname+"/assets/feelsbadman48.png",title:"FeelsGoodMan",map:c});var b=new google.maps.InfoWindow({content:"FeelsGoodMan"});a.setAnimation(google.maps.Animation.DROP);a.addListener("mouseover",function(){a.setIcon(location.protocol+"//"+location.hostname+"/assets/feelsgoodman48.png")});a.addListener("mouseout",function(){if(a.getAnimation()==null){a.setIcon(location.protocol+"//"+location.hostname+"/assets/feelsbadman48.png")}});a.addListener("click",function(){if(a.getAnimation()!==null){a.setAnimation(null);b.close()}else{a.setAnimation(google.maps.Animation.BOUNCE);b.open(c,a)}})};